student:
from var personItem in A.person
 select {
 id: (personItem?.id).toString(),
firstName: personItem.firstName,
lastName: personItem.lastName,
age: check (personItem.age).ensureType(),
types_new: personItem.types_new,
courses: from var coursesItem in personItem?.college?.courses ?: []
 select {
 colors: coursesItem.colors?:[],
id: check int:fromString(coursesItem?.id?:""),
credits: check (coursesItem.credits).ensureType(),
address: {
 city: coursesItem.address?.city,
street: coursesItem.address.street,
zipcode: coursesItem.address.zipcode 
},
professor: from var professorItem in coursesItem?.professor ?: []
 select {
 types: professorItem.types,
id: professorItem.id,
firstName: professorItem.firstName,
department: {
 color: professorItem?.department?.color?:"BLUE",
departmentName: professorItem?.department?.departmentName?:"" 
}
}
}
}
