        });
        if address.houseNo == "" {
            while address.houseNo != "" {
                address.houseNo = getRandomHome();
            }
        } else if address.street == "" {
            match name {
                "john" => {
                    io:println("name list");
                }
                "wick" => {
                    log:printInfo(string `${address.street} + ${name}`);
                }
                _ => {
                }
            }
        } else {
        }
        string agentRes = check aiAgent.run(string `${name} user asking following questions`);
        string sampleRes = check aiAgentResult.run("sample query");
        fork {
            worker worker1 {
                do {
                    lock {
                    }
                } on fail error err {
                }
            }
            worker worker2 {
            }
        }
        map<any|error> waitResult = wait {worker1, worker2};
        foreach string var1 in [] {
            if agentRes == "" {
                return;
            } else {
                break;
            }
            if var1 {
                continue;
            }
        }

    } on fail error e {
        log:printError("Error occurred", 'error = e);
        return e;
    }
}

